import { Component } from '@angular/core';

@Component({
  selector: 'app-article',
  templateUrl: './article.component.html',
  styleUrls: ['./article.component.css'],
})
export class ArticleComponent {
  title: string = 'Whatever you wanna be';
  content: string = 'Only you have control of who you are.';
  isTechRelated: boolean = true;
}
<div class="container">
  <div class="row">
    <div class="col-xs-12">
      <h1 class="display-3 title-header" [ngStyle]="{backgroundColor: isTechRelated ? 'blue' : 'yellow'}">{{ title }}</h1>
      <hr>
      <p>{{ content }}</p>
      <hr>
      <button class="btn btn-primary" (click)="isTechRelated = !isTechRelated">Change isTechRelated</button>
      <hr>
      <p *ngIf="isTechRelated">Tech Related</p>
    </div>
  </div>
</div>
